'''Database logic for ACL management'''

from loki.db import db_cdn_system as db
from loki.base.models import ModelMixin


class DomainList(db.Model, ModelMixin):
    __tablename__ = 'domainlist'

    domain = db.Column(db.String(128), primary_key=True)
    users = db.Column(db.Text, nullable=False)
    update_time = db.Column('updateTime', db.DateTime, nullable=False)
